import React, { useEffect, useRef, useState } from 'react';
import 'boxicons';

const Home = () => {
  const textAnimatedRef = useRef(null);
  const blobRef = useRef(null);
  const imageRef = useRef(null);
  const [isHovering, setIsHovering] = useState(false);
  const textArr = ["FullStack Dev", "Java Engineer", "Laravel Developer"];

  // Gunakan useRef untuk menyimpan state yang tidak memicu render ulang
  const currTextIndexRef = useRef(0);
  const letterIndexRef = useRef(0);
  const textRef = useRef("");

  // Refs untuk tracking posisi mouse/touch
  const mousePosition = useRef({ x: 0, y: 0 });
  const initialPosition = useRef({ x: 0, y: 0 });

  const addLetter = () => {
    if (!textAnimatedRef.current) return;

    letterIndexRef.current++;
    if (letterIndexRef.current <= textRef.current.length) {
      if (textAnimatedRef.current) {
        textAnimatedRef.current.textContent = textRef.current.substring(0, letterIndexRef.current);
      }
      setTimeout(addLetter, 100);
    } else {
      setTimeout(removeLetter, 2000);
    }
  };

  const removeLetter = () => {
    if (!textAnimatedRef.current) return;

    letterIndexRef.current--;
    if (letterIndexRef.current >= 0) {
      if (textAnimatedRef.current) {
        textAnimatedRef.current.textContent = textRef.current.substring(0, letterIndexRef.current);
      }
      setTimeout(removeLetter, 100);
    } else {
      updateText();
    }
  };

  const updateText = () => {
    currTextIndexRef.current++;
    if (currTextIndexRef.current >= textArr.length) {
      currTextIndexRef.current = 0;
    }
    textRef.current = textArr[currTextIndexRef.current];
    letterIndexRef.current = 0;
    addLetter();
  };

  // Fungsi untuk menangani pergerakan 3D
  const handleMouseMove = (e) => {
    if (!blobRef.current || !imageRef.current) return;

    const { clientX, clientY } = e;
    const blob = blobRef.current.getBoundingClientRect();
    const centerX = blob.left + blob.width / 2;
    const centerY = blob.top + blob.height / 2;

    // Hitung pergerakan relatif terhadap pusat blob
    const moveX = (clientX - centerX) / 25;
    const moveY = (clientY - centerY) / 25;

    // Terapkan transformasi 3D
    imageRef.current.style.transform = `
      translate3d(${moveX}px, ${moveY}px, 0)
      rotateX(${-moveY}deg)
      rotateY(${moveX}deg)
    `;
  };

  const handleMouseEnter = () => {
    setIsHovering(true);
    if (imageRef.current) {
      imageRef.current.style.transition = 'transform 0.2s ease-out';
    }
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
    if (imageRef.current) {
      imageRef.current.style.transition = 'transform 0.5s ease-out';
      imageRef.current.style.transform = 'translate3d(0, 0, 0) rotateX(0deg) rotateY(0deg)';
    }
  };

  // Fungsi untuk menangani sentuhan pada perangkat mobile
  const handleTouchStart = (e) => {
    if (!blobRef.current || !imageRef.current) return;

    const touch = e.touches[0];
    initialPosition.current = { x: touch.clientX, y: touch.clientY };
    mousePosition.current = { x: 0, y: 0 };

    if (imageRef.current) {
      imageRef.current.style.transition = 'transform 0.2s ease-out';
    }
  };

  const handleTouchMove = (e) => {
    if (!blobRef.current || !imageRef.current) return;

    const touch = e.touches[0];
    const moveX = (touch.clientX - initialPosition.current.x) / 15;
    const moveY = (touch.clientY - initialPosition.current.y) / 15;

    mousePosition.current = { x: moveX, y: moveY };

    // Batasi pergerakan
    const limitedX = Math.max(-10, Math.min(10, moveX));
    const limitedY = Math.max(-10, Math.min(10, moveY));

    // Terapkan transformasi 3D
    imageRef.current.style.transform = `
      translate3d(${limitedX}px, ${limitedY}px, 0)
      rotateX(${-limitedY}deg)
      rotateY(${limitedX}deg)
    `;
  };

  const handleTouchEnd = () => {
    if (imageRef.current) {
      imageRef.current.style.transition = 'transform 0.5s ease-out';
      imageRef.current.style.transform = 'translate3d(0, 0, 0) rotateX(0deg) rotateY(0deg)';
    }
  };

  // Fungsi untuk menangani efek ripple pada tombol
  const handleButtonClick = (e) => {
    const button = e.currentTarget;
  };

  useEffect(() => {
    // Pastikan DOM sudah siap sebelum memulai animasi
    const timer = setTimeout(() => {
      if (textAnimatedRef.current) {
        textRef.current = textArr[0];
        textAnimatedRef.current.textContent = "";
        addLetter();
      }
    }, 100);

    return () => clearTimeout(timer);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <section className="home bd-grid" id="home" style={{ marginBottom: '65px' }}>
      <div className="home__data">
        <h1 className="home__title">
          Hi all,<br />
          I'am <span className="home__title-color">Bagas</span><br />
          <p className="textAnimated" ref={textAnimatedRef}></p>
        </h1>
        <a href="/assets/img/CV-Bagas Pratama Junianika.pdf" className="button">CV</a>
      </div>

      <div className="home__social">
        <a href="https://www.linkedin.com/in/bagas-pratama-junianika" className="home__social-icon">
          <box-icon type='logo' name='linkedin'></box-icon>
        </a>
        <a href="https://github.com/bagaspra16" className="home__social-icon">
          <box-icon type='logo' name='github'></box-icon>
        </a>
        <a href="https://www.youtube.com/channel/UC9Rb7-D7vhEviJUqIQxA9bg" className="home__social-icon">
          <box-icon type='logo' name='youtube'></box-icon>
        </a>
        <a href="#skills" className="home__social-icon">
          <svg width="24" height="24" viewBox="0 0 50 52" fill="none" xmlns="http://www.w3.org/2000/svg" className="laravel-icon">
            <path d="M49.626 11.564a.809.809 0 0 1 .028.209v10.972a.8.8 0 0 1-.402.694l-9.209 5.302V39.25c0 .286-.152.55-.4.694L20.42 51.01c-.044.025-.092.041-.14.058-.018.006-.035.017-.054.022a.805.805 0 0 1-.41 0c-.022-.006-.042-.018-.063-.026-.044-.016-.09-.03-.132-.054L.402 39.944A.801.801 0 0 1 0 39.25V6.334c0-.072.01-.142.028-.21.006-.023.02-.044.028-.067.015-.042.029-.085.051-.124.015-.026.037-.047.055-.071.023-.032.044-.065.071-.093.023-.023.053-.04.079-.06.029-.024.055-.05.088-.069h.001l9.61-5.533a.802.802 0 0 1 .8 0l9.61 5.533h.002c.032.02.059.045.088.068.026.02.055.038.078.06.028.029.048.062.072.094.017.024.04.045.054.071.023.04.036.082.052.124.008.023.022.044.028.068a.809.809 0 0 1 .028.209v20.559l8.008-4.611v-10.51c0-.07.01-.141.028-.208.007-.024.02-.045.028-.068.016-.042.03-.085.052-.124.015-.026.037-.047.054-.071.024-.032.044-.065.072-.093.023-.023.052-.04.078-.06.03-.024.056-.05.088-.069h.001l9.611-5.533a.801.801 0 0 1 .8 0l9.61 5.533c.034.02.06.045.09.068.025.02.054.038.077.06.028.029.048.062.072.094.018.024.04.045.054.071.023.039.036.082.052.124.009.023.022.044.028.068zm-1.574 10.718v-9.124l-3.363 1.936-4.646 2.675v9.124l8.01-4.611zm-9.61 16.505v-9.13l-4.57 2.61-13.05 7.448v9.216l17.62-10.144zM1.602 7.719v31.068L19.22 48.93v-9.214l-9.204-5.209-.003-.002-.004-.002c-.031-.018-.057-.044-.086-.066-.025-.02-.054-.036-.076-.058l-.002-.003c-.026-.025-.044-.056-.066-.084-.02-.027-.044-.05-.06-.078l-.001-.003c-.018-.03-.029-.066-.042-.1-.013-.03-.03-.058-.038-.09v-.001c-.01-.038-.012-.078-.016-.117-.004-.03-.012-.06-.012-.09v-.002-21.481L4.965 9.654 1.602 7.72zm8.81-5.994L2.405 6.334l8.005 4.609 8.006-4.61-8.006-4.608zm4.164 28.764l4.645-2.674V7.719l-3.363 1.936-4.646 2.675v20.096l3.364-1.937zM39.243 7.164l-8.006 4.609 8.006 4.609 8.005-4.61-8.005-4.608zm-.801 10.605l-4.646-2.675-3.363-1.936v9.124l4.645 2.674 3.364 1.937v-9.124zM20.02 38.33l11.743-6.704 5.87-3.35-8-4.606-9.211 5.303-8.395 4.833 7.993 4.524z" fill="currentColor" />
          </svg>
        </a>
      </div>

      <div 
        className="home__img"
        ref={blobRef}
        onMouseMove={isHovering ? handleMouseMove : null}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
      >
        <svg className="home__blob" viewBox="0 0 479 467">
          <defs>
            <filter id="shadow3D" x="-20%" y="-20%" width="140%" height="140%">
              <feDropShadow dx="2" dy="2" stdDeviation="3" flood-opacity="0.3" />
            </filter>
          </defs>
          <image 
            className="home__blob-img" 
            ref={imageRef}
            x="100" 
            y="0" 
            href="/assets/img/photo3.png"
            style={{
              transformStyle: 'preserve-3d',
              transition: 'transform 0.5s ease-out',
              transformOrigin: 'center center',
              filter: 'url(#shadow3D)'
            }}
          />
        </svg>
      </div>
    </section>
  );
};

export default Home;
